#!/bin/bash
ICON=""
echo "$ICON" | base64 -d > /root/amiconnect-painel-frontend/img/icon.png

# Nav - trocar logo.png pelo icone + texto
sed -i 's|<img src="img/logo.png" alt="AmiConnect" style="height:50px;">|<div style="display:flex;align-items:center;gap:8px;"><img src="img/icon.png" alt="AmiConnect" style="height:40px;"><span style="font-size:1.3rem;font-weight:700;"><span style="color:#1e3a5f;">Ami</span><span style="color:#5cb85c;">Connect</span></span></div>|g' /root/amiconnect-painel-frontend/dashboard.html /root/amiconnect-painel-frontend/cardapio.html /root/amiconnect-painel-frontend/conversas.html /root/amiconnect-painel-frontend/pedidos.html

# Login - trocar logo.png pelo icone + texto
sed -i 's|<img src="img/logo.png" alt="AmiConnect" style="height:80px; margin: 0 auto 8px; display:block;">|<div style="text-align:center;margin-bottom:16px;"><img src="img/icon.png" alt="AmiConnect" style="height:70px;margin:0 auto 8px;display:block;"><div style="font-size:1.8rem;font-weight:700;"><span style="color:#1e3a5f;">Ami</span><span style="color:#5cb85c;">Connect</span></div></div>|g' /root/amiconnect-painel-frontend/index.html

echo "√çcone instalado!"
